  const dispatch = useDispatch()
  const [isActive, setActive] = useState(false);
  const [data, setData] = useState([])
  const[serverResponse, setServerResponse] = useState('')
  const [sortByLabel, setSortByLabel] = useState('Sort by...')
  const toggleClass = () => {
    setActive(!isActive);
  };

