        <div className="nav-item" onClick={() => { sort('DESC') }}>Oldest to newest</div>
        <div className="nav-item" onClick={() => { sort('today') }}>Today</div>
        <div className="nav-item" onClick={() => { sort('NORMAL') }}>Normal</div>
      </div>
    </div>
  )
}

export default Bydate
